import {useState} from 'react';
const AddTransaction=() =>
{
    const [text,setText]=useState("");
    const [amount,setAmount]=useState(0);
    const changeText=(e) =>{
        setText(e.target.value);
    }
    const changeAmount= (e) =>{
        setAmount(e.target.value)
    }
    const onSubmit=(e) =>{
            e.preventDefault();
            const transaction={
                text,
                amount:parseInt(amount),
                id:Math.floor(Math.random()*100000)
            }

    }
    return(
        <>
        <h3>Add Transaction</h3>
        <form onSubmit={onSubmit}>
            <div className="form-control">
                    <label htmlFor="text">
                        Text
                    </label><br></br>
                    <input type="text" value={text} onChange={changeText}  placeholder="Enter Name..."></input>
            </div>
            <div className="form-control">
                <label>Add Amount <br></br> "Income-Positive","Expense-Negative"</label>
                <input type="number" onChange={changeAmount} placeholder="Enter Amount ..."></input>

            </div>
        </form>
        </>
    )
}
export default AddTransaction;
