const AppReducer= (state,action) =>{
    switch(action.type){
        case "DELETE_TRANSACTION":
                return{
                    ...state,transactions:state.transactions.filter((t) => t.id!==action.payload)
                }
        case 'ADD_TRANSACTION':
                return{
                    ...state,transactions:[...state]
                }
        default:
            return state;
    }
}
export default AppReducer;
